    public static function replacingPeriodsCommasAndExclamationPoints($s){
        $s= str_replace('"','', $s);
        $s= str_replace(':','', $s); 
        $s= str_replace('.','', $s); 
        $s= str_replace(',','', $s); 
        $s= str_replace(';','', $s); 
        $s= str_replace('”','', $s); 
        $s= str_replace(')','', $s);
        $s= str_replace('(','', $s);  
        $s= str_replace('<','', $s);  
        $s= str_replace('>','', $s);  
        $s= str_replace('/','', $s);
        return $s;
    }

